mapper.select:
  		select ROWNUM,T.* FROM(SELECT A.SUB_CODE,A.PRE_CAPA,A.CAPACITY,B.SUB_NAME,B.COMPLETE_PT,B.SUB_YEAR,B.T_SEMESTER,C.PROF_NAME,B.T_DEPT,B.SUB_DATE,
  		B.SUB_TIME,B.SUB_ROOM,A.OPEN_YN,B.SUB_TYPE
        from HD_class_TB A,HD_SUBJECT_TB B,HD_PROFESSOR_TB C
        WHERE A.SUB_CODE=B.SUB_CODE AND A.PROF_ID=C.PROF_ID
        ORDER BY A.OPEN_YN DESC)T 
        where 1=1
        
			AND sub_type = #{chk_isu}
		
		
		
			AND SUBSTR(SUB_CODE,0,1) = #{chk_school}
		
		
		
			AND t_dept = #{chk_dept}
		
		
		 
			AND SUB_YEAR = #{chk_year}
		
		
		 
			AND T_SEMESTER = #{chk_sem}
		
		
		 
			AND SUB_NAME LIKE '%'||#{chk_subName}||'%'

  		select count(*) from(select ROWNUM,T.* FROM(SELECT A.SUB_CODE,A.PRE_CAPA,A.CAPACITY,B.SUB_NAME,B.COMPLETE_PT,B.SUB_YEAR,B.T_SEMESTER,C.PROF_NAME,B.T_DEPT,B.SUB_DATE,
  		B.SUB_TIME,B.SUB_ROOM,A.OPEN_YN,B.SUB_TYPE
        from HD_class_TB A,HD_SUBJECT_TB B,HD_PROFESSOR_TB C
        WHERE A.SUB_CODE=B.SUB_CODE AND A.PROF_ID=C.PROF_ID
        ORDER BY A.OPEN_YN DESC)T 
        where 1=1
        
			AND sub_type = #{chk_isu}
		
		
		
			AND SUBSTR(SUB_CODE,0,1) = #{chk_school}
		
		
		
			AND t_dept = #{chk_dept}
		
		
		 
			AND SUB_YEAR = #{chk_year}
		
		
		 
			AND T_SEMESTER = #{chk_sem}
		
		
		 
			AND SUB_NAME LIKE '%'||#{chk_subName}||'%'
		
           )
